sourcesContent:
{{quest === undefined ? 'quest empty': null}}
<h1 class="text-5xl font-bold">{{quest.title}}</h1>
<p class="text-sm text-gray-500 mb-4">{{quest.goal}}</p>
<div class="text-lg text-white mb-6" [innerHTML]="quest.description"></div>
<div class="steps">
  <div class="ml-8">
    <ng-container *ngFor="let step of quest.steps; let i = index;">
      <div class="flex mb-2">
        <div class="w-6 h-6 rounded-full mr-2 flex items-center justify-center text-white bg-gold">{{i+1 + "."}}</div>
        <div class="text-lg text-gray-300">{{step}}</div>
      </div>
    </ng-container>
  </div>
</div>
<div class="rewards mt-8" *ngIf="quest.rewards">
  <h2 class="text-xl text-gold font-bold mb-2">Rewards:</h2>
  <div class="ml-8">
    <ng-container *ngFor="let reward of quest.rewards">
      <div class="text-lg text-gray-300">{{reward}}</div>
    </ng-container>
  </div>
</div>
import { Component, Input } from "@angular/core";
import Quest from "src/common/interfaces/quest.interface";

@Component({
  selector: "app-quest",
  templateUrl: "./quest.component.html",
  styleUrls: ["./quest.component.scss"],
})
export class QuestComponent {
  // @Input() quest!: Quest;

  quest: Quest = {
    title: "The Lost Artifact",
    description:
      "The great treasure hunter, Indiana Jones, has recently discovered a mysterious artifact buried deep within the ruins of an ancient temple. The artifact, known as the Eye of Horus, is said to possess incredible powers that could change the course of history. However, just as he was about to retrieve it, the temple suddenly collapsed, trapping the Eye of Horus inside. Now, Indiana Jones needs your help to retrieve the artifact before it falls into the wrong hands. You must travel through treacherous jungles, navigate deadly traps, and face off against ruthless enemies to retrieve the Eye of Horus. Will you be able to overcome these challenges and retrieve the artifact before it's too late?",
    goal: "Retrieve the Eye of Horus artifact from the ruins of the ancient temple.",
    steps: [
      "Travel to the jungle where the temple is located.",
      "Find a way into the temple and navigate through its deadly traps.",
      "Face off against the temple's guardians to retrieve the artifact.",
      "Escape the temple before it collapses and traps you inside forever.",
    ],
    rewards: [
      "The Eye of Horus artifact, which possesses incredible powers.",
      "A significant amount of gold and treasure from the temple.",
      "The admiration and respect of Indiana Jones himself.",
    ],
    isCompleted: false,
  };
}
