{{quest === undefined ? 'quest empty': null}}
<h1 class="text-5xl font-bold text-gold">{{quest.title}}</h1>
<p class="text-sm text-gray-500 mb-4">{{quest.goal}}</p>
<div class="text-lg text-white mb-6" [innerHTML]="quest.description"></div>
<div class="steps">
  <h2 class="text-xl text-gold font-bold mb-2">Steps:</h2>
  <ul class="list-disc ml-8">
    <li class="text-lg text-gray-300 mb-2" *ngFor="let step of quest.steps">{{step}}</li>
  </ul>
</div>
<div class="rewards mt-8" *ngIf="quest.rewards">
  <h2 class="text-xl text-gold font-bold mb-2">Rewards:</h2>
  <ul class="list-disc ml-8">
    <li class="text-lg text-gray-300 mb-2" *ngFor="let reward of quest.rewards">{{reward}}</li>
  </ul>
</div>
import { Component, Input } from "@angular/core";
import Quest from "src/common/interfaces/quest.interface";

@Component({
  selector: "app-quest",
  templateUrl: "./quest.component.html",
  styleUrls: ["./quest.component.scss"],
})
export class QuestComponent {
  // @Input() quest!: Quest;

  quest: Quest = {
    title: "Rescue the Princess",
    description:
      "The Princess has been kidnapped by the evil dragon, go rescue her!",
    goal: "Defeat the dragon and rescue the Princess",
    steps: [
      "Travel to the dragon's lair",
      "Defeat the dragon in battle",
      "Rescue the Princess from the dragon's clutches",
      "Return the Princess safely to her castle",
    ],
    rewards: ["The gratitude of the Princess", "A bag of gold"],
    isCompleted: false,
  };
}
